import {
  StyleSheet,
  Text,
  View,
  StatusBar,
  Image
} from 'react-native';
import Foto from './assets/Ferraz.png'


export default function App() {
  return ( <
    View style = {
      styles.container
    } >
    <
    StatusBar style = "auto" / >
    <
    View style = {
      styles.header
    } >

    <
    View style = {
      styles.headerCampos
    } >
    <
    View style = {
      styles.headerIcones
    } >
    <
    Image source = {
      Foto
    }
    style = {
      styles.foto
    }
    /> <
    /View> <
    View style = {
      styles.headerIcones
    } >
    <
    Text style = {
      styles.texto
    } > icones < /Text>

    <
    /View> <
    /View>

    <
    Text style = {
      styles.texto
    } > Olá, Leandro < /Text>

    <
    /View> <
    View style = {
      styles.container2
    } >
    <
    Text style = {
      styles.texto
    } > Saldo da conta < /Text>

    <
    /View> <
    View style = {
      styles.container3
    } >
    <
    Text style = {
      styles.texto
    } > Funcionalidades < /Text>

    <
    /View> <
    View style = {
      styles.container4
    } >
    <
    Text style = {
      styles.texto
    } > Meus cartões < /Text>

    <
    /View> <
    View style = {
      styles.container5
    } >
    <
    Text style = {
      styles.texto
    } > Mensagens < /Text>

    <
    /View> <
    View style = {
      styles.container6
    } >
    <
    Text style = {
      styles.texto
    } > Cartão de crédito < /Text>

    <
    /View> <
    View style = {
      styles.footer
    } >
    <
    Text style = {
      styles.texto
    } > Footer < /Text>

    <
    /View> <
    /View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
  },
  texto: {
    fontSize: 14,
    color: '#fff',
  },
  header: {
    height: '20%',
    width: '100%',
    padding: 15,
    justifyContent: 'flex-start',
    backgroundColor: '#901abb',
    flexDirection: 'row',
  },
  headerIcones: {
    width: '50%',
    backgroundColor: 'black'
  },
  headerCampos: {
    width: '50%',
    backgroundColor: 'black',
    flexDirection: 'row',
  },
  foto: {
    width: 50,
    height: 50,
    marginRight: 15,
    size: 20,
    color: "#000",
    marginBottom: 14,
  },
  container2: {
    height: '15%',
    width: '100%',
    backgroundColor: 'red'
  },
  container3: {
    height: '15%',
    width: '100%',
    backgroundColor: 'blue'
  },
  container4: {
    height: '15%',
    width: '100%',
    backgroundColor: 'green'
  },
  container5: {
    height: '10%',
    width: '100%',
    backgroundColor: 'white'
  },
  container6: {
    height: '20%',
    width: '100%',
    backgroundColor: 'gray'
  },
  footer: {
    height: '5%',
    width: '100%',
    backgroundColor: '#131313'
  }


});
